import { useState, useEffect, useContext, Fragment } from 'react';
import axios from 'axios';
import { UserOutlined } from '@ant-design/icons';
import moment from 'moment';

import UserContext from '../context';

const Account = ({ history }) => {
    const [state, setState] = useContext(UserContext);
    const [subscriptions, setSubscriptions] = useState([]);

    const getSubscriptions = async () => {
        const { data: { data } } = await axios.get('/subscriptions');
        setSubscriptions(data);
    };

    const manageSubscriptions = () => {

    };

    useEffect(() => {
        if (!state || !state.token) return;
        getSubscriptions();
    }, [state]);

    return (
        <div className="container">
            <div className="row">
                <UserOutlined className="display-4" />
                <h1>Account</h1>
                <p className="lead pb-4">Subscription Status</p>
            </div>

            <div className="row">
                {subscriptions?.map(sub => (
                    <div key={sub.id}>
                        <section>
                            <hr />
                            <h4 className="fw-bold">{sub.plan.nickname}</h4>
                            <h5>
                                {(sub.plan.amount / 100).toLocaleString("en-US", {
                                    style: "currency",
                                    currency: sub.plan.currency
                                })}
                            </h5>
                            <p>Status: {sub.status}</p>
                            <p>Card last 4 digit: {sub.default_payment_method.card.last4}</p>
                            <p>
                                Current Period end: {
                                    moment(sub.current_period_end * 1000)
                                        .format('dddd, MMMM Do YYYY h:mm:ss')
                                        .toString()
                                }
                            </p>
                            {sub.status === 'active' ? (
                                <Fragment>
                                    <button 
                                        className="btn btn-info p-2"
                                        onClick={() => history.push(`/${sub.plan.nickname.toLowerCase()}`)}
                                    >
                                        Access
                                    </button>
                                    <button 
                                        className="btn btn-warning p-2 mx-4"
                                        onClick={manageSubscriptions}
                                    >
                                        Manage Subscription
                                    </button>
                                </Fragment>
                            ) : ""}
                        </section>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Account;
