const PriceCard = ({ price }) => {
    const dynamicDescription = price => {
        if (price.nickname === 'BASIC') return "5 execlusive stocks";
        if (price.nickname === 'STANDARD') return "10 execlusive stocks";
        if (price.nickname === 'PREMIUM') return "15 execlusive stocks";
    };

    const handleSubscription = price => {};
    return (
        <div className="col">
            <div className="card mb-4 rounded-3 shadow-sm">
                <div className="card-header py-3">
                    <h4 className="my-0">{price.nickname}</h4>
                </div>
                <div className="card-body">
                    <h1 className="card-title pricing-card-title">
                        {(price.unit_amount / 100).toLocaleString("en-US", {
                            style: "currency",
                            currency: "USD"
                        })} <small className="text-muted fw-light">/ Month</small>
                    </h1>
                    <ul className="list-unstyled mt-3 mb-4">
                        <li className="p-2 fw-bold">{dynamicDescription(price)}</li>
                        <li className="p-2">Free Market Analysis</li>
                        <li className="p-2">Email Support</li>
                        <li className="p-2">Help Center Access</li>
                    </ul>

                    <button className="w-100 btn btn-lg btn-outline-success" onClick={() => handleSubscription(price)}>
                        Sign Up
                    </button>
                </div>
            </div>
        </div>
  );
};

export default PriceCard;
